import "./App.css";
import { useState, useEffect } from "react";
import Header from "./components/Header";
import Shelves from "./components/Shelves";
import * as BookAPI from './BooksAPI';
import Book from "./components/Book";
import { BrowserRouter as Router, Switch , Route,L } from "react-router-dom";


function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);

  const [books, setBooks] = useState([]);

  const [search, setSearch] = useState("");

  const [searchBook, setSearchBook] = useState([]);

  const [mergeBook, setMergeBook] = useState([]);

  const [mapOfIdToBooks, setMapOfIdToBooks] = useState(new Map());

  const handleInputChange = (event) => {
    const type = event.target.value;
    setSearch(type);
  }

  useEffect(() => {
    BookAPI.getAll().then(data => {
        setBooks(data)
        setMapOfIdToBooks(createMapOfBooks(data));
      });
  }, [])

  useEffect(() => {
    let isActive = true;

    if (search){
      BookAPI.search(search).then(data => {
        if (data.error) {
          setSearchBook([]);
        } else {
          if (isActive){
            console.log(data)
            setSearchBook(data);
          }
        }
      })
    }

    return () => {
      isActive = false;
      setSearchBook([])
      console.log("Unmount", search);
    }
  }, [search]);

  useEffect(() => {
    const combined = searchBook.map(book => {
      if (mapOfIdToBooks.has(book.id)) {
        return mapOfIdToBooks.get(book.id);
      } else {
        return book;
      }
    })
    setMergeBook(combined);
  }, [searchBook])

  const createMapOfBooks = (books) => {
    const map= new Map();
    books.map(book => map.set(book.id, book));
    return map;
  }

  const updateBookShelf = (book, whereTo) => {
    const updateBooks = books.map(b => {
      if (b.id === book.id) {
        book.shelf = whereTo;
        return book;
      }
      return b;
    })
    setBooks(updateBooks)
    BookAPI.update(book, whereTo)
  };

  return (
    <div className="app">
      <Router>
        <Switch>
          <Route>
            {/* Search Page */}
            <div className="search-books">
              <div className="search-books-bar">
                <a
                  className="close-search"
                  onClick={() => setShowSearchpage(!showSearchPage)}
                >
                  Close
                </a>
                <div className="search-books-input-wrapper">
                  <input type="text" placeholder="Search by title, author, or ISBN" value={search} onChange={handleInputChange} />
                </div>
              </div>
              <div className="search-books-results">
                <ol className="books-grid">
                  {mergeBook.map(b => (
                    <li key={b.id}>
                        <Book book={b} updateBookShelf={updateBookShelf}/>
                    </li>
                  ))}
                </ol>
              </div>
            </div>
          </Route>

          <Route path="/">       
          {/* Main Page */}
            <div className="list-books">
              <Header />
              <div className="list-books-content">
                <Shelves books={books} updateBookShelf={updateBookShelf} />
              </div>
              <div className="open-search">
                <a>Add a book</a>
              </div>
            </div>
          </Route> 
        </Switch>
      </Router>
    </div>
  );
};

export default App;
