import "./App.css";
import { useState, useEffect } from "react";
import Header from "./components/Header";
import Shelves from "./components/Shelves";
import * as BookAPI from './BooksAPI';
import Book from "./components/Book";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);

  const [books, setBooks] = useState([]);

  const [search, setSearch] = useState("");

  const [searchBook, setSearchBook] = useState([]);

  const handleInputChange = (event) => {
    const type = event.target.value;
    setSearch(type);
  }

  useEffect(() => {
    BookAPI.getAll()
      .then(data => {
        setBooks(data)
      });
  }, [])

  useEffect(() => {
    let isActive = true;

    if (search){
      BookAPI.search(search).then(data => {
        if (data.error) {
          console.log(data);
        } else {
          if (isActive){
            setSearchBook(data);
          }
        }
      })
    }

    return () => {
      isActive = false;
      
      console.log("Unmount", search);
    }
  }, [search]);

  const updateBookShelf = (book, whereTo) => {
    const updateBooks = books.map(b => {
      if (b.id === book.id) {
        book.shelf = whereTo;
        return book;
      }
      return b;
    })
    setBooks(updateBooks)
    BookAPI.update(book, whereTo)
  };

  return (
    <div className="app">
      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a
              className="close-search"
              onClick={() => setShowSearchpage(!showSearchPage)}
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input type="text" placeholder="Search by title, author, or ISBN" value={search} onChange={handleInputChange} />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid">
              {searchBook.map(b => (
                <li key={b.id}>
                    <Book book={b} updateBookShelf={updateBookShelf}/>
                </li>
              ))}
            </ol>
          </div>
        </div>
      ) : (
        <div className="list-books">
          <Header />
          <div className="list-books-content">
            <Shelves books={books} updateBookShelf={updateBookShelf} />
          </div>
          <div className="open-search">
            <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
